function add_demo_content(){
    // add users 
    $users = get_demo_users();
     
    foreach($users as $key => $user ){

        if ( !email_exists($user[1]) && !username_exists('user_'.$key) )
        $new_user = wp_insert_user(
            array(
                'user_login'=>'user_'.$key ,
                'user_pass'=>'1234' ,
                'display_name'=>$user[0],
                'user_email'=>$user[1],
            ) );
    }


    $attachments = get_demo_attachments();
    $books = get_demo_books();

    foreach($books as $book){
        // add attachments 
        
         
        // add book 
        $loream_text = 'Neque porro quisquam est qui dolorem ipsum quia dolor sit amet, consectetur, adipisci ';
        $book_description = str_repeat( $loream_text , rand(3,10) );
        $user_key = array_rand($users);
        
        $user_wp = username_exists( 'user_'.$user_key );

        $new_book = array(
                'post_author'   =>  $user_wp ,
                'post_title'    =>  $book[0],
                'post_status'   =>  'publish',
                'post_content'  =>  $book_description,
                'post_type'     =>  'shelf_book',
                );
        $book_id = wp_insert_post($new_book   ); 
        // link attachment 
        set_post_thumbnail( $book_id, $attachments[ array_rand($attachments) ] );
        
        // meta
        update_post_meta($book_id,'book_author',$book[1]);
        update_post_meta($book_id,'publication_year',$book[2]);
        update_post_meta($book_id,'book_genre', $book[3]);
        if (count ($users[$user_key][2])){
            $user_collections = $users[$user_key][2]; 
            $collection_name = $user_collections[array_rand($user_collections)];
            $collection_id = book_category($collection_name , $user_wp);
            wp_set_post_terms($book_id, $collection_id, 'book_category', true);
            //update_post_meta($book_id,'book_collection', $user_collections[array_rand($user_collections)]);
        }

        // ADD to wishlist 
        $rand_users_number = rand(5 , 19);
        $random_users = array_rand($users, $rand_users_number );
        //var_dump($random_users); die ;
        foreach ( $random_users as $rand_user_key){
             // bulk add .......
            $user_wp = email_exists($users[$rand_user_key][1]) ;
            add_post_meta($book_id , 'book_wishlist' , $user_wp);
        }
        
           
            
    }
    

    
}

function convert_cover_attachment($image_url) {
    require_once ABSPATH . 'wp-admin/includes/file.php';
    require_once ABSPATH . 'wp-admin/includes/media.php';
    require_once ABSPATH . 'wp-admin/includes/image.php';

    // Download the image from the URL and create an attachment.
    $attachment_id = media_sideload_image($image_url, 0,null,'id');

    return $attachment_id;
}

function get_demo_attachments(){
    $attachmets=[];
    for( $i=1 ; $i<19 ; $i++)
        $attachmets[] =  convert_cover_attachment(BookShelf_PLUGIN_URL.'assets/covers/cover ('.$i.').jpg');
    return $attachmets; 
}

